INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (1, 'Bronco Badlands', 'bronco-badlands', 'La SUV con el equilibrio perfecto entre comodidad, seguridad y estilo', 'Ícono de la aventura', 'Desempeño único, diseño icónico y tecnología inteligente: la nueva Bronco es la mejor compañera de aventuras; La nueva BRONCO Badlands 4 puertas,  ofrece un incomparable nivel de desempeño con su motor 2.7L EcoBoost® Bi-Turbo V6, que genera una potencia de 330 caballos de fuerza y un torque de 415 lb-pie', 102883.26);
INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (2, 'Bronco Sport', 'bronco-sport', 'Con su característico diseño robusto, el ícono de la aventura.', 'Nueva Bronco Sport', 'Con su característico diseño robusto, el ícono de la aventura se renueva completamente con tecnologías de vanguardia, consolidando su posición como el compañero ideal para cualquier expedición. Impulsado por un motor EcoBoost® 2.0L, este vehículo entrega 249 caballos de fuerza y 277 libras-pie de torque, garantizando un rendimiento excepcional en cada trayecto.', 65228.47);
INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (3, 'Everest Active', 'everest-active', 'La combinación perfecta de comodidad, tecnología y desempeño.', 'Nueva Ford Everest', 'Prepárate para redefinir tus aventuras. La nueva Everest Active se presenta como la encarnación perfecta de la robustez y la sofisticación, fusionando una comodidad insuperable, tecnología de vanguardia y un desempeño imparable. Diseñada para ir más allá de lo ordinario, esta SUV ofrece un rendimiento robusto y eficiente que te asegura estar listo para enfrentar cualquier desafío que la carretera, o la ausencia de ella, te presente.', 74612.89);
INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (4, 'Everest Titanium', 'everest-titanium', 'La combinación perfecta de comodidad, tecnología y desempeño.', '', 'Prepárate para redefinir tus aventuras. La nueva Everest Titanium se presenta como la encarnación perfecta de la robustez y la sofisticación, fusionando una comodidad insuperable, tecnología de vanguardia y un desempeño imparable. Diseñada para ir más allá de lo ordinario, esta SUV ofrece un rendimiento robusto y eficiente que te asegura estar listo para enfrentar cualquier desafío que la carretera, o la ausencia de ella, te presente.', 84362.91);
INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (5, 'Expedition', 'expedition', 'Ideal para tu familia, la combinación perfecta de tecnología, espacio y seguridad.', 'Lleva tu aventura a otro nivel', 'Conoce la camioneta ideal para tu familia, con una combinación perfecta de tecnología avanzada, espacio flexible y mayor seguridad.', 129284.03);
INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (6, 'Explorer Platinum', 'explorer-platinum', 'Redescubre un ícono de la familia Ford, que se redefine con un diseño más elegante y juvenil.', 'Nueva SUV Explorer Platinum', 'Diseñada para satisfacer las necesidades de los conductores más exigentes, la Explorer 2025 es la SUV con el equilibrio perfecto entre comodidad, seguridad y estilo. Redescubre un ícono de la familia Ford, que se redefine con un diseño más elegante y juvenil.', 92669.10);
INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (7, 'Ranger Raptor', 'ranger-raptor', 'Con capacidad para conquistar cualquier terreno', 'Nueva Ford Ranger Raptor - Segunda Generación', 'La Nueva Ranger Raptor está diseñada para dominar en el desierto, las montañas y elevar el nivel de rendimiento todoterreno al ser una Pick- Up para los verdaderos entusiastas del Off- Road.', 84228.38);
INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (8, 'Ranger Wildtrak', 'ranger-wildtrak', 'La pickup que redefine los estándares de diseño, robustez, tecnología y seguridad', 'Nueva Generación RANGER', 'Prepárate para redefinir lo que esperas de una pickup. La Ford Ranger Wildtrak 2025 ha sido desarrollada con la fuerza para enfrentar cualquier desafío, convirtiéndose en la compañera ideal para el trabajo más exigente y las aventuras más audaces. No es solo una pickup; es la representación de la fusión perfecta entre capacidad y diseño, donde la robustez se une a una estética moderna y funcional. Con la Ranger 2025, el enfoque va más allá de simplemente llegar a tu destino. Se trata de disfrutar cada kilómetro del viaje, incluso cuando el camino se vuelve difícil y las condiciones exigen lo máximo. Su ingeniería avanzada y su performance superior te brindan la confianza para superar obstáculos, llevando tu experiencia de manejo a un nuevo nivel. ¡Descúbrela y siente la diferencia de una pickup que está lista para todo!', 69824.48);
INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (9, 'Ranger XLT', 'ranger-xlt', 'La pickup que redefine los estándares de diseño, robustez, tecnología y seguridad', 'Nueva Generación RANGER', 'Prepárate para redefinir lo que esperas de una pickup. La Ford Ranger XLT 2025 ha sido desarrollada con la fuerza para enfrentar cualquier desafío, convirtiéndose en la compañera ideal para el trabajo más exigente y las aventuras más audaces. No es solo una pickup; es la representación de la fusión perfecta entre capacidad y diseño, donde la robustez se une a una estética moderna y funcional. Con la Ranger 2025, el enfoque va más allá de simplemente llegar a tu destino. Se trata de disfrutar cada kilómetro del viaje, incluso cuando el camino se vuelve difícil y las condiciones exigen lo máximo. Su ingeniería avanzada y su performance superior te brindan la confianza para superar obstáculos, llevando tu experiencia de manejo a un nuevo nivel. ¡Descúbrela y siente la diferencia de una pickup que está lista para todo!', 57883.27);
INSERT INTO `cars` (`id`, `name`, `code`, `header`, `title`, `description`, `price`) VALUES (10, 'Territory Trend', 'territory-trend', 'La SUV más potente, equipada, cómoda y segura del segmento', 'La SUV que tú defines', 'Disfruta de viajes seguros y placenteros con tu familia gracias al amplio espacio y moderno diseño de Ford Territory Trend 2025 , SUV de Gran Tecnología que ofrece todo lo que necesitas para convertir cada viaje en una experiencia inolvidable.', 46106.97);
